(function(){"use strict";var cookieAlert=document.querySelector(".cookiealert");var acceptCookies=document.querySelector(".acceptcookies");if(!cookieAlert){return}cookieAlert.offsetHeight;if(!getCookie("acceptCookies")){cookieAlert.classList.add("show")}acceptCookies.addEventListener("click",function(){setCookie("acceptCookies",true,365);cookieAlert.classList.remove("show");window.dispatchEvent(new Event("cookieAlertAccept"))});function setCookie(cname,cvalue,exdays){let d=new Date();d.setTime(d.getTime()+(exdays*24*60*60*1000));let expires="expires="+d.toUTCString();document.cookie=cname+"="+cvalue+";"+expires+";path=/"}function getCookie(cname){let name=cname+"=";let decodedCookie=decodeURIComponent(document.cookie);let ca=decodedCookie.split(';');for(let i=0;i<ca.length;i+=1){let c=ca[i];while(c.charAt(0)===' '){c=c.substring(1)}if(c.indexOf(name)===0){return c.substring(name.length,c.length)}}return ""}})();